mapper.select:
SELECT SEQ, ID, NAME, (SELECT CD_NAME FROM SIX_COMMON_CODE A WHERE A.CD = REQUEST_CD) REQUEST_NAME, IMAGE, POST, INTRODUCE, SLOGAN, (SELECT CD_NAME FROM SIX_COMMON_CODE A WHERE A.CD = BANK_CD) BANK_NAME, ACCOUNT_NO, ACCOUNT_NAME, CREATE_DATE, IS_CONFIRM, UPDATE_USER_ID, UPDATE_DATE FROM SIX_PARTNER WHERE 1=1
AND NAME = #{name}
ORDER BY SEQ DESC
LIMIT #{start},#{end}

mapper.update:
UPDATE SIX_PARTNER
REQUEST_CD=#{requestCd}, IMAGE=#{image}, POST=post, INTRODUCE=#{introduce}, SLOGAN=#{slogan}, BANK_CD=#{bankCd}, ACCOUNT_NO=#{accountNo}, ACCOUNT_NAME=#{accountName}, IS_CONFIRM=#{isConfirm}, UPDATE_USER_ID = #{updateUserId}, UPDATE_DATE = now()
WHERE SEQ = #{seq}

UPDATE SIX_COMMON_CODE
CD_NAME=#{cdName}, UPDATE_USER_ID = #{updateUserId}, UPDATE_DATE = now()
WHERE SEQ = #{seq}

mapper.select:
    SELECT COUNT(*)
	  FROM SIX_AUTHORITY
	 WHERE ID = #{id}
	   AND AUTHORITY = #{authority}

mapper.insert:
INSERT INTO SIX_AUTHORITY(
                          ID, 
                          AUTHORITY
                         )
                   VALUES(
                          #{id},
                          #{authority}
                         )
