mapper.select:
SELECT SEQ, NO, TITLE, CONTENTS, CREATE_USER_ID, CREATE_DATE, UPDATE_USER_ID, UPDATE_DATE, HIT_COUNT, IS_NOTICE, FILE, THUMB_IMAGE, REF, INDENT, STEP, (SELECT A.PARTNER_NAME FROM SIX_PARTNER A WHERE A.PARTNER_CD = PARTNER_CD) PARTNER_NAME, (SELECT A.TYPE_NAME FROM SIX_PARTNER A WHERE A.TYPE_CD = TYPE_CD) TYPE_NAME, CASE WHEN PRICE = 0 THEN '무료' ELSE PRICE END AS PRICE FROM ${boardName} WHERE 1=1
AND REF = #{ref}
AND TITLE LIKE CONCAT('%', #{title} , '%')
AND CONTENTS LIKE CONCAT('%', #{contents} , '%')
ORDER BY NO DESC
LIMIT #{start},#{end}

SELECT SEQ, TITLE, CONTENTS, CREATE_USER_ID, CREATE_DATE, UPDATE_USER_ID, UPDATE_DATE, HIT_COUNT, IS_NOTICE, REF, INDENT, STEP, FILE, THUMB_IMAGE
,STOCK_NAME
FROM ${boardName} WHERE 1=1
AND SEQ = #{seq}
AND REF = #{ref}

mapper.update:
UPDATE ${boardName}
TITLE=#{title}, CONTENTS=#{contents}, HIT_COUNT=HIT_COUNT+1, IS_NOTICE=#{isNotice}, REF=#{ref}, INDENT=#{indent}, STEP=#{step}, FILE=#{file}, THUMB_IMAGE=#{thumbImage}, STOCK_NAME=#{stockName}, UPDATE_USER_ID = #{updateUserId}, UPDATE_DATE = now()
WHERE SEQ = #{seq}

 	 	UPDATE ${boardName} A, (SELECT SEQ
    	                          FROM ${boardName}
    	                         WHERE SEQ IN 
    	                         
					                   #{item}
					  	  	     
					  	  	    ) B                        
           SET A.STEP = A.STEP + 1 
         WHERE A.SEQ IN (B.SEQ)
           AND STEP >= #{step}

    	UPDATE ${boardName} A, (SELECT SEQ
    	                          FROM ${boardName}
    	                         WHERE SEQ IN 
    	                         
					                   #{item}
					  	  	     
					  	  	    ) B                        
           SET A.STEP = A.STEP - #{cnt} 
         WHERE A.REF IN (B.SEQ)
           AND STEP >= #{step}

