mapper.select:
SELECT COUNT(*)		       
  FROM ${boardName}
 WHERE 1=1
 
   AND REF = #{ref}
 
 
   AND TITLE LIKE CONCAT('%', #{title} , '%')		   
  
 
   AND CONTENTS LIKE CONCAT('%', #{contents} , '%')

SELECT SEQ, TITLE, CONTENTS, CREATE_USER_ID, CREATE_DATE, UPDATE_USER_ID, UPDATE_DATE, HIT_COUNT, IS_NOTICE, FILE, THUMB_IMAGE FROM ${boardName} WHERE 1=1
AND REF = #{ref}
AND TITLE LIKE CONCAT('%', #{title} , '%')
AND CONTENTS LIKE CONCAT('%', #{contents} , '%')
ORDER BY SEQ DESC
LIMIT #{start},#{end}

mapper.update:
UPDATE ${boardName}
TITLE=#{title}, CONTENTS=#{contents}, HIT_COUNT=HIT_COUNT+1, IS_NOTICE=#{isNotice}, REF=#{ref}, INDENT=#{indent}, STEP=#{step}, FILE=#{file}, THUMB_IMAGE=#{thumbImage}, UPDATE_USER_ID = #{updateUserId}, UPDATE_DATE = now()
WHERE SEQ = #{seq}
